mapper.select:
SELECT COUNT(*) FROM (SELECT * FROM social 
where 1=1
	AND CATEGORI = #{categori}
	AND title LIKE '%' || #{keyword} || '%'
	AND id LIKE '%' || #{keyword} || '%'
	AND (TITLE LIKE '%' || #{keyword} || '%') or (CONTENT LIKE '%' || #{keyword} || '%')
	AND resort = #{resort}
	AND OFFERDATE between TO_char(#{offerdate}, 'YYYYMMDD') AND TO_CHAR(#{offerdateEnd}, 'YYYYMMDD')
	AND complete='N'
) WHERE  SNO > 0
